import React, { useEffect, useRef, useState } from 'react';
import MapGL, { Layer, Source } from 'react-map-gl';
import 'mapbox-gl/dist/mapbox-gl.css';

import { getCountryData, getUSAData } from './services';

export function Map() {
  const [viewport, setViewport] = useState({
    latitude: 40.67,
    longitude: -103.59,
    zoom: 1,
    bearing: 0,
    pitch: 0,
  });
  const [clusterData, setClusterData] = useState(null);
  const sourceRef = useRef();

  // Get GEO data when component mounts
  useEffect(() => {
    const _geoData = async () => {
      const { data: countryData } = await getCountryData();
      const formattedCountryData = countryData
        .filter(({ country }) => country !== 'USA')
        .map(
          ({
            cases,
            country,
            countryInfo: { lat, long },
            deaths,
            recovered,
          }) => ({
            geometry: {
              coordinates: [long, lat],
              type: 'Point',
            },
            properties: {
              cases,
              country,
              deaths,
              recovered,
            },
            type: 'Feature',
          }),
        );

      const { data: usaData } = await getUSAData();
      const formattedUSAData = usaData.map(
        ({
          city,
          coordinates: { latitude, longitude },
          province,
          stats: { confirmed, deaths, recovered },
        }) => ({
          geometry: {
            coordinates: [longitude, latitude],
            type: 'Point',
          },
          properties: {
            cases: parseInt(confirmed),
            city,
            country: 'USA',
            deaths: parseInt(deaths),
            recovered: parseInt(recovered),
            state: province,
          },
          type: 'Feature',
        }),
      );

      setClusterData({
        features: [...formattedCountryData, ...formattedUSAData],
        type: 'FeatureCollection',
      });
    };

    _geoData();
  }, []);

  const _onViewportChange = updatedViewport => setViewport(updatedViewport);

  const _onClick = event => {
    const feature = event.features[0];
    const clusterId = feature.properties.cluster_id;

    const mapboxSource = sourceRef.current.getSource();

    mapboxSource.getClusterExpansionZoom(clusterId, (err, zoom) => {
      if (err) return;

      _onViewportChange({
        latitude: feature.geometry.coordinates[1],
        longitude: feature.geometry.coordinates[0],
        transitionDuration: 500,
        viewport,
        zoom,
      });
    });
  };

  const clusterLayer = {
    filter: ['all', ['has', 'cases'], ['>', 'cases', 0]],
    id: 'cluster-circle',
    paint: {
      'circle-color': '#f44336',
      'circle-opacity': 0.3,
      'circle-radius': ['step', ['get', 'cases'], 5, 20, 6, 150, 8],
    },
    source: 'cluster-circle',
    type: 'circle',
  };

  return (
    <MapGL
      {...viewport}
      dragRotate={false}
      height="100%"
      interactiveLayerIds={[clusterLayer.id]}
      mapStyle="mapbox://styles/mapbox/dark-v9"
      mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_GL_API_TOKEN}
      onClick={_onClick}
      onViewportChange={_onViewportChange}
      width="100%"
    >
      <Source
        clusterMaxZoom={5}
        clusterRadius={32}
        data={clusterData}
        ref={sourceRef}
        type="geojson"
      >
        <Layer {...clusterLayer} />
      </Source>
    </MapGL>
  );
}
