import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

import { Map } from './Map';
import { PageTemplate } from './PageTemplate';

import styles from './App.css'; // eslint-disable-line

export const paths = {
  map: {
    name: 'Map',
    path: '/',
  },
  news: {
    name: 'News',
    path: '/news',
  },
};

function App() {
  const [totals, setTotals] = useState({
    active: 0,
    cases: 0,
    deaths: 0,
    prevActive: 0,
    prevCases: 0,
    prevDeaths: 0,
    prevRecovered: 0,
    recovered: 0,
    updated: null,
  });

  // Get total counts when component mounts
  useEffect(() => {
    const _totalData = async () => {
      const {
        data: { active, cases, deaths, recovered, updated },
      } = await getTotals();

      setTotals({
        active,
        cases,
        deaths,
        prevActive: totals.active,
        prevCases: totals.cases,
        prevDeaths: totals.deaths,
        prevRecovered: totals.recovered,
        recovered,
        updated,
      });
    };

    _totalData();
  }, []); // eslint-disable-line

  return (
    <PageTemplate totals={totals}>
      <Router>
        <Switch>
          <Route exact path={paths.map.path}>
            <Map />
          </Route>

          <Route exact path={paths.news.path}>
            <div>hi</div>
          </Route>
        </Switch>
      </Router>
    </PageTemplate>
  );
}

export default App;
