import React, { useEffect, useRef, useState } from 'react';
import Container from '@material-ui/core/Container';
import Divider from '@material-ui/core/Divider';
import Grid from '@material-ui/core/Grid';
import RootRef from '@material-ui/core/RootRef';
import Typography from '@material-ui/core/Typography';
import grey from '@material-ui/core/colors/grey';
import { makeStyles } from '@material-ui/core/styles';
import {
  ChartLabel,
  Crosshair,
  FlexibleWidthXYPlot,
  HorizontalGridLines,
  LineSeries,
  VerticalGridLines,
  XAxis,
  YAxis,
} from 'react-vis';
import moment from 'moment';
import numeral from 'numeral';

import { DataTable } from './DataTable';
import { getCountries, getHistory } from './services';
import { rootStyles } from './App';

const useStyles = makeStyles(() => ({
  divider: {
    marginBottom: '16px',
  },
  header: {
    marginBottom: '12px',
  },
  root: {
    ...rootStyles,
    backgroundColor: grey[100],
  },
}));

export function Dashboard() {
  const classes = useStyles();

  const [countryTableBodyRows, setCountryTableBodyRows] = useState([]);
  const [todayTableBodyRows, setTodayTableBodyRows] = useState([]);
  const todayTableRef = useRef(null);
  const [history, setHistory] = useState([]);

  // Get data for tables and charts when component mounts
  useEffect(() => {
    const _countryData = async () => {
      const { data } = await getCountries();

      const countryTableData = data.map(
        ({ active, cases, country, deaths, recovered }) => ({
          id: country,
          country,
          active,
          deaths,
          recovered,
          cases,
        }),
      );
      setCountryTableBodyRows(countryTableData);

      const todayTableData = data.map(
        ({ country, todayCases, todayDeaths }) => ({
          id: country,
          country,
          todayDeaths,
          todayCases,
        }),
      );
      setTodayTableBodyRows(todayTableData);
    };

    const _historyData = async () => {
      const { data } = await getHistory();

      const historyChartData = data.filter(({ country }) => country === 'usa');
      setHistory(historyChartData);
    };

    _countryData();
    _historyData();
  }, []); // eslint-disable-line

  const countryTableHeadCells = [
    { id: 'country', label: 'Country' },
    {
      id: 'active',
      label: 'Active Cases',
    },
    { id: 'deaths', label: 'Deaths' },
    { id: 'recovered', label: 'Recovered' },
    { id: 'cases', label: 'Total Confirmed' },
  ];
  const todayTableHeadCells = [
    { id: 'country', label: 'Country' },
    { id: 'todayDeaths', label: 'Deaths' },
    { id: 'todayCases', label: 'Cases' },
  ];

  console.log(history);

  return (
    <article className={classes.root}>
      <Container>
        <Grid container spacing={3}>
          {/* Today table */}
          <Grid item xs={12} md={5}>
            <DashboardHeader>Confirmed Today</DashboardHeader>

            <RootRef rootRef={todayTableRef}>
              <DataTable
                bodyRows={todayTableBodyRows}
                headCells={todayTableHeadCells}
                initialOrder="desc"
                initialOrderBy="todayCases"
              />
            </RootRef>
          </Grid>

          {/* History chart */}
          <Grid item xs={12} md={7}>
            <DashboardHeader>USA Case History</DashboardHeader>

            <HistoryChart
              height={
                todayTableRef.current ? todayTableRef.current.offsetHeight : 300
              }
              history={history}
            />
          </Grid>

          {/* Country overview table */}
          <Grid item xs={12}>
            <DashboardHeader>Country Overview</DashboardHeader>

            <DataTable
              bodyRows={countryTableBodyRows}
              headCells={countryTableHeadCells}
              initialOrder="desc"
              initialOrderBy="active"
            />
          </Grid>
        </Grid>
      </Container>
    </article>
  );
}

function DashboardHeader({ children }) {
  const classes = useStyles();

  return (
    <>
      <Typography className={classes.header} component="h2" variant="h5">
        {children}
      </Typography>
      <Divider className={classes.divider} />
    </>
  );
}

function HistoryChart({ height, history }) {
  const [crosshairValue, setCrosshairValue] = useState(null);

  if (!history.length) return null;

  return (
    <FlexibleWidthXYPlot
      height={height}
      onMouseLeave={() => {
        setCrosshairValue(null);
      }}
    >
      <VerticalGridLines />
      <HorizontalGridLines />
      <ChartLabel
        includeMargin={false}
        text="Date"
        xPercent={0.015}
        yPercent={1.01}
      />
      <XAxis
        tickFormat={x => moment(x).format('MM/DD/YYYY')}
        // tickLabelAngle={-45}
        tickTotal={5}
      />

      <ChartLabel
        includeMargin={false}
        style={{
          transform: 'rotate(-90)',
          textAnchor: 'end',
        }}
        text="# Cases"
        xPercent={0.03}
        yPercent={0.06}
      />
      <YAxis tickFormat={y => numeral(y).format('0a')} tickTotal={5} />

      {history.map(({ country, timeline: { cases } }) => {
        const data = Object.keys(cases).map(date => ({
          x: moment(date).valueOf(),
          y: cases[date],
        }));

        return (
          <LineSeries
            curve={'curveMonotoneX'}
            data={data}
            onNearestX={d => {
              setCrosshairValue(d);
            }}
          />
        );
      })}

      {crosshairValue && <Crosshair values={[crosshairValue]} />}
    </FlexibleWidthXYPlot>
  );
}
