import React, { useEffect, useState } from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import grey from '@material-ui/core/colors/grey';
import { makeStyles } from '@material-ui/core/styles';
import {
  HorizontalGridLines,
  LineMarkSeries,
  VerticalGridLines,
  XAxis,
  XYPlot,
  YAxis,
} from 'react-vis';

import { DataTable } from './DataTable';
import { getCountries, getHistory } from './services';
import { rootStyles } from './App';

const useStyles = makeStyles(() => ({
  root: {
    ...rootStyles,
    backgroundColor: grey[100],
  },
}));

export function Dashboard() {
  const classes = useStyles();

  const [countries, setCountries] = useState([]);
  const [history, setHistory] = useState([]);

  // Get country and history data when component mounts
  useEffect(() => {
    const _countryData = async () => {
      const { data } = await getCountries();
      setCountries(data);
    };

    const _historyData = async () => {
      const { data } = await getHistory();
      setHistory(data);
    };

    _countryData();
    _historyData();
  }, []); // eslint-disable-line

  const headCells = [
    {
      id: 'name',
      label: 'Dessert (100g serving)',
    },
    { id: 'calories', label: 'Calories' },
    { id: 'fat', label: 'Fat (g)' },
    { id: 'carbs', label: 'Carbs (g)' },
    { id: 'protein', label: 'Protein (g)' },
  ];

  function createData(name, calories, fat, carbs, protein) {
    return { name, calories, fat, carbs, protein };
  }

  const rowCells = [
    createData('Cupcake', 305, 3.7, 67, 4.3),
    createData('Donut', 452, 25.0, 51, 4.9),
    createData('Eclair', 262, 16.0, 24, 6.0),
    createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
    createData('Gingerbread', 356, 16.0, 49, 3.9),
    createData('Honeycomb', 408, 3.2, 87, 6.5),
    createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
    createData('Jelly Bean', 375, 0.0, 94, 0.0),
    createData('KitKat', 518, 26.0, 65, 7.0),
    createData('Lollipop', 392, 0.2, 98, 0.0),
    createData('Marshmallow', 318, 0, 81, 2.0),
    createData('Nougat', 360, 19.0, 9, 37.0),
    createData('Oreo', 437, 18.0, 63, 4.0),
  ];

  return (
    <article className={classes.root}>
      <Container>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <DataTable headCells={headCells} rowCells={rowCells} />
          </Grid>
        </Grid>
      </Container>
    </article>
  );
}

function LineChart(props) {
  return (
    <XYPlot width={300} height={300}>
      <VerticalGridLines />
      <HorizontalGridLines />
      <XAxis />
      <YAxis />
      <LineMarkSeries
        className="linemark-series-example"
        style={{
          strokeWidth: '3px',
        }}
        lineStyle={{ stroke: 'red' }}
        markStyle={{ stroke: 'blue' }}
        data={[
          { x: 1, y: 10 },
          { x: 2, y: 5 },
          { x: 3, y: 15 },
        ]}
      />
      <LineMarkSeries
        className="linemark-series-example-2"
        curve={'curveMonotoneX'}
        data={[
          { x: 1, y: 11 },
          { x: 1.5, y: 29 },
          { x: 3, y: 7 },
        ]}
      />
    </XYPlot>
  );
}
