import React, { useState } from 'react';
import _ from 'lodash';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import TableSortLabel from '@material-ui/core/TableSortLabel';
import { makeStyles } from '@material-ui/core/styles';

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

// const headCells = [
//   {
//     id: 'name',
//     label: 'Dessert (100g serving)',
//   },
//   { id: 'calories', label: 'Calories' },
//   { id: 'fat', label: 'Fat (g)' },
//   { id: 'carbs', label: 'Carbs (g)' },
//   { id: 'protein', label: 'Protein (g)' },
// ];

const rows = [
  createData('Cupcake', 305, 3.7, 67, 4.3),
  createData('Donut', 452, 25.0, 51, 4.9),
  createData('Eclair', 262, 16.0, 24, 6.0),
  createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
  createData('Gingerbread', 356, 16.0, 49, 3.9),
  createData('Honeycomb', 408, 3.2, 87, 6.5),
  createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
  createData('Jelly Bean', 375, 0.0, 94, 0.0),
  createData('KitKat', 518, 26.0, 65, 7.0),
  createData('Lollipop', 392, 0.2, 98, 0.0),
  createData('Marshmallow', 318, 0, 81, 2.0),
  createData('Nougat', 360, 19.0, 9, 37.0),
  createData('Oreo', 437, 18.0, 63, 4.0),
];

const useStyles = makeStyles(theme => ({
  paper: {
    marginBottom: theme.spacing(2),
    width: '100%',
  },
  root: {
    width: '100%',
  },
  table: {
    minWidth: 750,
  },
  visuallyHidden: {
    border: 0,
    clip: 'rect(0 0 0 0)',
    height: 1,
    margin: -1,
    overflow: 'hidden',
    padding: 0,
    position: 'absolute',
    top: 20,
    width: 1,
  },
}));

export function DataTable({ headCells }) {
  const classes = useStyles();

  const [order, setOrder] = useState('asc');
  const [orderBy, setOrderBy] = useState('calories');
  const [page, setPage] = useState(0);
  const pageSizes = [10, 25, 50];
  const [rowsPerPage, setRowsPerPage] = useState(pageSizes[0]);

  const _requestSort = (event, property) => {
    const isAsc = orderBy === property && order === 'asc';
    setOrder(isAsc ? 'desc' : 'asc');
    setOrderBy(property);
  };

  const _changePage = (event, newPage) => {
    setPage(newPage);
  };

  const _changeRowsPerPage = event => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const emptyRows =
    rowsPerPage - Math.min(rowsPerPage, rows.length - page * rowsPerPage);

  return (
    <div className={classes.root}>
      <Paper className={classes.paper}>
        <TableContainer>
          <Table
            className={classes.table}
            aria-labelledby="tableTitle"
            size="small"
            aria-label="data table"
          >
            <DataTableHead
              cells={headCells}
              classes={classes}
              onRequestSort={_requestSort}
              order={order}
              orderBy={orderBy}
            />
            <TableBody>
              {_.orderBy(rows, [orderBy], [order])
                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                .map(({ calories, carbs, fat, name, protein }) => (
                  <TableRow hover key={name} tabIndex={-1}>
                    <TableCell component="th" scope="row">
                      {name}
                    </TableCell>
                    <TableCell align="right">{calories}</TableCell>
                    <TableCell align="right">{fat}</TableCell>
                    <TableCell align="right">{carbs}</TableCell>
                    <TableCell align="right">{protein}</TableCell>
                  </TableRow>
                ))}

              {emptyRows > 0 && (
                <TableRow style={{ height: 33 * emptyRows }}>
                  <TableCell colSpan={6} />
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>

        <TablePagination
          rowsPerPageOptions={pageSizes}
          component="nav"
          count={rows.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onChangePage={_changePage}
          onChangeRowsPerPage={_changeRowsPerPage}
        />
      </Paper>
    </div>
  );
}

function DataTableHead({ cells, classes, onRequestSort, order, orderBy }) {
  const _createSortHandler = property => event => {
    onRequestSort(event, property);
  };

  return (
    <TableHead>
      <TableRow>
        {cells.map(({ id, label }, index) => (
          <TableCell
            align={index === 0 ? 'left' : 'right'}
            key={id}
            sortDirection={orderBy === id ? order : false}
          >
            <TableSortLabel
              active={orderBy === id}
              direction={orderBy === id ? order : 'asc'}
              onClick={_createSortHandler(id)}
            >
              {label}
              {orderBy === id ? (
                <span className={classes.visuallyHidden}>
                  {order === 'desc' ? 'sorted descending' : 'sorted ascending'}
                </span>
              ) : null}
            </TableSortLabel>
          </TableCell>
        ))}
      </TableRow>
    </TableHead>
  );
}
