import React, { createElement, useEffect, useState } from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import grey from '@material-ui/core/colors/grey';
import { makeStyles } from '@material-ui/core/styles';

import { NewsCard } from './NewsCard';
import { getNews } from './services';

const useStyles = makeStyles(theme => ({
  root: {
    backgroundColor: grey[200],
    flexGrow: 1,
    minHeight: `calc(100vh - 60px)`,
    paddingBottom: '16px',
    paddingTop: '16px',
  },

  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));

export function News() {
  const classes = useStyles();

  const [newsArticles, setNewsArticles] = useState([]);

  useEffect(() => {
    const _newsData = async () => {
      const {
        data: { articles, totalResults },
      } = await getNews({ page: 1 });

      const articlesWithGridSizes = articles.map((article, index) => {
        let componentType, gridSize;
        switch (index) {
          default:
          case 0:
            componentType = Paper;
            gridSize = 12;
            break;

          case 1:
          case 2:
            componentType = NewsCard;
            gridSize = 6;
            break;

          case 3:
          case 4:
          case 5:
          case 6:
            componentType = NewsCard;
            gridSize = 3;
            break;

          case 7:
          case 8:
          case 9:
            componentType = NewsCard;
            gridSize = 4;
            break;
        }

        return { ...article, componentType, gridSize };
      });
      setNewsArticles(articlesWithGridSizes);
    };

    _newsData();
  }, []);

  return (
    <article className={classes.root}>
      <Container>
        <Grid container spacing={3}>
          {newsArticles.map(({ componentType, gridSize, title, ...data }) => {
            const renderedComponent = createElement(componentType, {
              title,
              ...data,
            });

            return (
              <Grid item key={title} xs={gridSize}>
                {renderedComponent}
              </Grid>
            );
          })}
        </Grid>
      </Container>
    </article>
  );
}
