import React from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

import { Dashboard } from './Dashboard';
import { Map } from './Map';
import { News } from './News';
import { PageTemplate } from './PageTemplate';

import './App.css';
import '../node_modules/react-vis/dist/style.css';

export const paths = {
  dashboard: {
    name: 'Dashboard',
    path: '/dashboard',
  },
  map: {
    name: 'World Map',
    path: '/',
  },
  news: {
    name: 'News Feed',
    path: '/news',
  },
};

export const rootStyles = {
  flexGrow: 1,
  height: '100vh',
  overflowY: 'auto',
  paddingBottom: '16px',
  paddingTop: '16px',
};

function App() {
  return (
    <Router>
      <PageTemplate>
        <Switch>
          <Route exact path={paths.dashboard.path}>
            <Dashboard />
          </Route>

          <Route exact path={paths.map.path}>
            <Map />
          </Route>

          <Route exact path={paths.news.path}>
            <News />
          </Route>
        </Switch>
      </PageTemplate>
    </Router>
  );
}

export default App;
