import React, { useEffect, useState } from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import grey from '@material-ui/core/colors/grey';
import { makeStyles } from '@material-ui/core/styles';

import { NewsCard } from './NewsCard';
import { getNews } from './services';

const useStyles = makeStyles(theme => ({
  firstArticle: {
    height: '25vh',
  },
  root: {
    backgroundColor: grey[200],
    flexGrow: 1,
    minHeight: `calc(100vh - 60px)`,
    paddingBottom: '16px',
    paddingTop: '16px',
  },

  //   paper: {
  //     padding: theme.spacing(2),
  //     textAlign: 'center',
  //     color: theme.palette.text.secondary,
  //   },
}));

export function News() {
  const classes = useStyles();

  const [newsArticles, setNewsArticles] = useState([]);

  useEffect(() => {
    const _newsData = async () => {
      const {
        data: { articles, totalResults },
      } = await getNews({ page: 1 });

      setNewsArticles(articles);
    };

    _newsData();
  }, []);

  const [firstNewsArticle, ...articles] = newsArticles;

  return (
    <article className={classes.root}>
      <Container>
        <Grid container spacing={3}>
          {firstNewsArticle && (
            <Grid item xs={12}>
              <Paper
                className={classes.firstArticle}
                style={{ backgroundImage: firstNewsArticle.urlToImage }}
              ></Paper>
            </Grid>
          )}

          {articles.map(article => (
            <Grid item key={article.title} xs={3}>
              <NewsCard {...article} />
            </Grid>
          ))}
        </Grid>
      </Container>
    </article>
  );
}
