import React, { useState } from 'react';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import grey from '@material-ui/core/colors/grey';
import { makeStyles } from '@material-ui/core/styles';
import {
  ChartLabel,
  Crosshair,
  FlexibleWidthXYPlot,
  HorizontalGridLines,
  LineSeries,
  VerticalGridLines,
  XAxis,
  YAxis,
} from 'react-vis';
import moment from 'moment';
import numeral from 'numeral';

const useStyles = makeStyles(() => ({
  crosshair: {
    backgroundColor: grey[900],
    color: grey[100],
    padding: '8px',
  },
}));

export default function HistoryChart({ height, history }) {
  const classes = useStyles();

  const [crosshairValue, setCrosshairValue] = useState(null);

  const _formatDate = date => moment(date).format('MM/DD/YYYY');

  if (!history.length) return null;

  return (
    <FlexibleWidthXYPlot
      height={height}
      onMouseLeave={() => {
        setCrosshairValue(null);
      }}
    >
      <VerticalGridLines />
      <HorizontalGridLines />
      <ChartLabel
        includeMargin={false}
        text="Date"
        xPercent={0.015}
        yPercent={1.01}
      />
      <XAxis tickFormat={_formatDate} tickTotal={5} />

      <ChartLabel
        includeMargin={false}
        style={{
          transform: 'rotate(-90)',
          textAnchor: 'end',
        }}
        text="# Cases"
        xPercent={0.03}
        yPercent={0.06}
      />
      <YAxis tickFormat={y => numeral(y).format('0a')} tickTotal={5} />

      {history.map(({ timeline: { cases } }) => {
        const data = Object.keys(cases).map(date => ({
          x: moment(date).valueOf(),
          y: cases[date],
        }));

        return (
          <LineSeries
            curve={'curveMonotoneX'}
            data={data}
            onNearestX={d => {
              setCrosshairValue(d);
            }}
          />
        );
      })}

      {crosshairValue && (
        <Crosshair values={[crosshairValue]}>
          <Paper className={classes.crosshair}>
            <Typography component="p" variant="body2">
              <strong># Cases</strong>:{' '}
              {numeral(crosshairValue.y).format('0,0')}
            </Typography>
            <Typography component="p" variant="body2">
              <strong>Date</strong>: {_formatDate(crosshairValue.x)}
            </Typography>
          </Paper>
        </Crosshair>
      )}
    </FlexibleWidthXYPlot>
  );
}
