export const clusterLayer = {
  filter: ['get', 'cases'],
  id: 'clusters',
  paint: {
    'circle-color': '#51bbd6',
    'circle-radius': ['step', ['get', 'point_count'], 20, 100, 30, 750, 40],
  },
  source: 'cases',
  type: 'circle',
};

export const clusterCountLayer = {
  filter: ['get', 'cases'],
  id: 'cluster-count',
  layout: {
    'text-field': '{point_count_abbreviated}',
    'text-font': ['DIN Offc Pro Medium', 'Arial Unicode MS Bold'],
    'text-size': 12,
  },
  source: 'cases',
  type: 'symbol',
};
